#include <iostream> using namespace std; // ulamek postaci xxxxxxxxxx.x (jedno miejsce po przecinku) class Ulamek {   int l; // licznik   int m; // miasownik public:   Ulamek() : Ulamek(0,1) {}   Ulamek(int _l,int _m) {l=_l;m=_m;}   friend ostream& operator<<(ostream &o, Ulamek d);   friend istream& operator>>(istream& i, Ulamek& d);   Ulamek& operator+=(Ulamek b) {     l = l*b.m+b.l*m;     m = m * b.m;     return *this;   }   Ulamek operator+(Ulamek b) {     Ulamek tmp = *this;     tmp += b;     return tmp;   }  }; ostream& operator<<(ostream &o, Ulamek d) {   o << d.l << "/" << d.m;   return o; }istream& operator>>(istream& i, Ulamek& d) {   i >> d.l;   char tmp;   i >> tmp;   i >> d.m;   return i; }int main() {   Ulamek a,b;   cout << "Podaj dwa ulamki dziesietne: " << endl;   cout << "a="; cin >> a;   cout << "b="; cin >> b;   cout << "Suma " << a << "+" << b << "=";   cout << (a+b);   return 0;}
